import './App.css';
import React from 'react';
import { Form , Input , Button } from 'antd';
import FormItem from 'antd/es/form/FormItem';



function App() {

  const Header = (props) => {

    let color = ''; 

    switch(props.type) {
      case 'SUBMIT':
      color = 'green';
      break;
      case 'cancel':
      color = 'red';
      break;
      default:
        color =""
    }

return (
  <Button style={{background:color}}>{props.text}</Button>
)
  }


  return (
    <div className="App">
     <Form>
      <FormItem label="User Name" name ="username">
        <Input placeholder='User Name' required></Input>
      </FormItem>
      <FormItem label="Password" name ="password">
        <Input placeholder='Password 'required></Input>
      </FormItem>
      <FormItem>
      <Header text ="LOG IN" type="SUBMIT"/>
      <Header text ="CANCEL" type="cancel"/>
        </FormItem>
     </Form>




    </div>
  );
}

export default App;
